def largest_integer(num):
        arr2 = []
        while num>0:
            arr2.append(num%10)
            num = num//10

        odds = []
        evens = []
        for i in range(len(arr2)):
            if arr2[i]%2 == 1:
                  odds.append(arr2[i])
            else:
                 evens.append(arr2[i])
        odds.sort(reverse=True)
        evens.sort(reverse=True)

        ret = 0
        for i in range(len(arr2)):
            if arr2[i]%2 == 0:
                ret*=10
                ret+= evens[0]
                evens.pop(0)
            else:
                ret*=10
                ret+=odds[0]
                odds.pop(0)

        return int(ret)

print(largest_integer())