fn largest_integer(mut num:i128)->i128{
    let mut arr2:Vec<i128>= vec![];
    while num>0{
        arr2.push(num%10);
        num = num/10;
    }

    let mut odds:Vec<i128>= vec![];
    let mut evens:Vec<i128>= vec![];
    for i in 0..arr2.len(){
        if arr2[i] % 2 == 1{
            odds.push(arr2[i]);
        }else{
            evens.push(arr2[i]);
        }
    }
    odds.sort();
    odds.reverse();
    evens.sort();
    evens.reverse();

    let mut ret:i128 = 0;
    for i in 0..arr2.len(){
        if arr2[i]%2==0{
            ret*=10;
            ret+=evens[0];
            evens.remove(0);
        }else{
            ret*=10;
            ret+=odds[0];
            odds.remove(0);
        }
    }
    
    ret
}

fn main(){
    println!("{}",largest_integer());
}